import React, { useEffect, useState } from "react"
import { useDispatch, useSelector } from "react-redux"
import { useParams, useNavigate } from "react-router-dom"
import styled from "styled-components"
import useInput from "../../hooks/useInput"
import { __getTodo } from "../../redux/modules/todoSlice"
import { FaPencilAlt, FaTrashAlt } from "react-icons/fa"

const DetailTodo = () => {
    const dispatch = useDispatch()
    const navigate = useNavigate()

    const [nickname, onChangeNicknameHandler, resetNickname] = useInput();
    const [content, onChangeContentHandler, resetContent] = useInput();

    const [isTodoEdit, setIsTodoEdit] = useState(false)

    const {isLoading, todo, error} = useSelector((state) => state.todo)
    const {id} = useParams()

    useEffect(()=> {
        dispatch(__getTodo(id))
    },[dispatch])

    if (isLoading) {
        return <div>로딩 중 ....</div>
    }

    if (error) {
        return <div>{error.message}</div>
    }

    return (
        <DetailTodoCtn>
            <DetailTop>
                <DetailHeader>
                    <DetailTodoID>ID: {id}</DetailTodoID>
                    <BackBtn onClick={()=>navigate("/todos")}>이전으로</BackBtn>
                </DetailHeader>
                <DetailBody>
                    <DetailBody2>
                        <DetailNickname>@{todo.nickname}</DetailNickname>
                        <DetailTitle>{todo.title}</DetailTitle>
                    </DetailBody2>
                    {isTodoEdit ? (
                        <DetailContent>
                            {todo.content}
                        </DetailContent>
                    ) : (
                        null
                    )


                    }
                </DetailBody>
            </DetailTop>
            <DetailTodoEdit>
                수정하기
            </DetailTodoEdit>
            <Footer>
                <Footer1>
                    댓글 보기
                </Footer1>
                <Footer2>
                    <AddCommentNickname
                        type="text"
                        name="nickname"
                        value={nickname}
                        onChange={onChangeNicknameHandler}
                        placeholder="작성자의 이름을 입력해주세요. (5자 이내)"
                        maxLength="5"
                        required
                    />
                    <AddCommentContent
                        type="text"
                        name="content"
                        value={content}
                        onChange={onChangeContentHandler}
                        placeholder="내용을 입력해주세요. (200자 이내)"
                        maxLength="200"
                        required
                    />
                    <AddCommentBtn>추가하기</AddCommentBtn>
                </Footer2>
                <CommentsCtn>
                    <CommentBox>
                        <CommentViewBox>
                            <CommentsNickname>@{"nickname1"}</CommentsNickname>
                            <CommentsContent>{"댓글이 여기에 보입니다.1"}</CommentsContent>
                        </CommentViewBox>
                        <CommentEditBox>
                            <CommentIcon
                                color="#1a73e8"
                            >
                                <FaPencilAlt
                                    size={20}
                                    color="white"
                                />
                            </CommentIcon>
                            <CommentIcon
                                color="tomato"
                            >
                                <FaTrashAlt
                                    size={20}
                                    color="white"
                                />
                            </CommentIcon>
                        </CommentEditBox>
                    </CommentBox>
                    <CommentBox>
                        <CommentViewBox>
                            <CommentsNickname>@{"nickname2"}</CommentsNickname>
                            <CommentsContent>{"댓글이 여기에 보입니다.2"}</CommentsContent>
                        </CommentViewBox>
                        <CommentEditBox>
                            <CommentIcon
                                color="#1a73e8"
                            >
                                <FaPencilAlt
                                    size={20}
                                    color="white"
                                />
                            </CommentIcon>
                            <CommentIcon
                                color="tomato"
                            >
                                <FaTrashAlt
                                    size={20}
                                    color="white"
                                />
                            </CommentIcon>
                        </CommentEditBox>
                    </CommentBox>
                    <CommentBox>
                        <CommentViewBox>
                            <CommentsNickname>@{"nickname3"}</CommentsNickname>
                            <CommentsContent>{"댓글이 여기에 보입니다.3"}</CommentsContent>
                        </CommentViewBox>
                        <CommentEditBox>
                            <CommentIcon
                                color="#1a73e8"
                            >
                                <FaPencilAlt
                                    size={20}
                                    color="white"
                                />
                            </CommentIcon>
                            <CommentIcon
                                color="tomato"
                            >
                                <FaTrashAlt
                                    size={20}
                                    color="white"
                                />
                            </CommentIcon>
                        </CommentEditBox>
                    </CommentBox>

                </CommentsCtn>
            </Footer>
        </DetailTodoCtn>
    )
}

const DetailTodoCtn = styled.div`
    margin: 20px auto 0 auto;
    box-sizing: border-box;
    padding: 20px;
    display: flex;
    gap: 20px;
    flex-direction: column;
    height: calc(100vh - 90px);
    justify-content: space-between;
`
const DetailTop = styled.div`
    display: flex;
    gap: 20px;
    flex-direction: column;
`
const DetailHeader = styled.div`
    font-size: 24px;
    display: flex;
    justify-content: space-between;
    align-items: center;
`
const DetailTodoID = styled.p`
    color: gray;
`
const BackBtn = styled.button`
    border: 2px solid #ddd;
    border-radius: 10px;
    padding: 5px 12px;
`
const DetailBody = styled.div`
    border-top: 2px solid #ddd;
    padding: 40px 0;
    display: flex;
    gap: 20px;
    flex-direction: column;
