import React from "react"
import styled from "styled-components"

const AddTodo = () => {

    return (
        <AddTodoCtn>
            <AddTodoBox>
                <AddTodoTitle>작성자</AddTodoTitle>
                <AddTodoInput />
            </AddTodoBox>
            <AddTodoBox>
                <AddTodoTitle>제목</AddTodoTitle>
                <AddTodoInput />
            </AddTodoBox>
            <AddTodoBox>
                <AddTodoTitle>내용</AddTodoTitle>
                <AddTodoTextarea />
            </AddTodoBox>
        </AddTodoCtn>
    )
}

const AddTodoCtn = styled.form`
    margin: 20px auto 0 auto;
    max-width: 500px;
    display: flex;
    flex-direction: column;
    gap: 20px;
    padding: 20px;
    box-sizing: border-box;
`
const AddTodoBox = styled.div`
    display: flex;
    flex-direction: column;
    gap: 10px;
    margin-bottom: 10px;
    box-sizing: border-box;
`
const AddTodoTitle = styled.h3`
    font-size: 24px;
    font-weight: 400;
`
const AddTodoInput = styled.input`
    border: 1px solid #ddd;
    border-radius: 10px;
    padding: 0 12px;
    height: 46px;
    font-size: 14px;
`
const AddTodoTextarea = styled.textarea`
    border: 1px solid #ddd;
    border-radius: 10px;
    padding: 12px;
    min-height: 120px;
    font-size: 14px;
`
export default AddTodo
