import { VscEdit, VscTrash } from "react-icons/vsc"
import React, { useEffect, useState } from "react"
import { useDispatch, useSelector } from "react-redux"
import styled from "styled-components"
import { useParams } from "react-router-dom"
import { __addComment, __deleteComment, __getComments } from "../../redux/modules/commentsSlice"


const Comment = () => {
    const dispatch = useDispatch()
    const {id} = useParams()
    const { comments } = useSelector((state) => state.comments)
    const [isEdit, setIsEdit] = useState(true)
    const thisComments = comments.filter((comment)=> {
        return +comment.todoId === +id
    })

    const onDeleteComment = (commentID) => {
        dispatch(__deleteComment(commentID))
    }

    return (
        <CommentsCtn>
            {thisComments.slice(0).reverse().map((comment)=> (
                (isEdit) ?  (
                    <CommentBox key={comment.id}>
                        <CommentViewBox>
                            <EditCommentInput />
                        </CommentViewBox>
                        <CommentEditBox>
                            <CommentIcon
                                color="#1a73e8"
                            >
                                수정
                            </CommentIcon>
                        </CommentEditBox>
                    </CommentBox>
                ) : (
                    <CommentBox key={comment.id}>
                        <CommentViewBox>
                            <CommentsNickname>@{comment.nickname}</CommentsNickname>
                            <CommentsContent>{comment.comment}</CommentsContent>
                        </CommentViewBox>
                        <CommentEditBox>
                            <CommentIcon
                                color="#1a73e8"
                            >
                                <VscEdit
                                    size={20}
                                    color="white"
                                />
                            </CommentIcon>
                            <CommentIcon
                                onClick={()=>onDeleteComment(comment.id)}
                                color="tomato"
                            >
                                <VscTrash
                                    size={20}
                                    color="white"
                                />
                            </CommentIcon>
                        </CommentEditBox>
                    </CommentBox>
                    )
                )
            )}
        </CommentsCtn>
    )
}

const CommentsCtn = styled.div`
    display: flex;
    flex-direction: column;
    padding: 5px 20px;
    gap: 10px;
    height: 190px;
    overflow: auto;
`
const CommentBox = styled.div`
    display: flex;
    gap: 10px;
    color: black;
    width: 100%;
    border-bottom: 1px solid #ddd;
`
const CommentViewBox = styled.div`
    display: flex;
    flex-direction: column;
    padding: 10px 0;
    gap: 10px;
    color: black;
    width: 100%;
`
const CommentsNickname = styled.p`
    font-size: 14px;
`
const CommentsContent = styled.p`
    font-size: 18px;
`
const CommentEditBox = styled.div`
    display: flex;
    align-items: center;
    gap: 10px;
`
const CommentIcon = styled.div`
    border-radius: 10px;
    font-size: 12px;
    color: white;
    width: 30px;
    height: 30px;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: ${(props) => props.color};
    &:hover {
        cursor: pointer;
    }
`
const EditCommentInput = styled.input`
    width: 100%;
    border: 1px solid #ddd;
    border-radius: 10px;
    padding: 0 12px;
    height: 30px;
    font-size: 12px;
`

export default Comment
