import React, { useEffect } from "react"
import styled from "styled-components"
import { FaTrashAlt } from "react-icons/fa"
import { useDispatch, useSelector } from "react-redux"
import { __getTodos } from "../../redux/modules/todosSlice"
const TodosList = () => {
    const dispatch = useDispatch()

    const {isLoading, todos, error} = useSelector((state) => state.todos)

    useEffect(()=> {
        dispatch(__getTodos())
    },[dispatch])

    if (isLoading) {
        return <div>로딩 중 ....</div>
    }

    if (error) {
        return <div>{error.message}</div>
    }

    return (
        <TodosListCtn>
            <HomeMenuName>
                Todo List
            </HomeMenuName>
            <TodosListBox>
                {todos.slice(0).reverse().map((todo)=> {
                    return (
                        <ListCtn key={todo.id}>
                            <ListCtn2>
                                <ListTitle>{todo.title}</ListTitle>
                                <ListNickname>@{todo.nickname}</ListNickname>
                            </ListCtn2>
                            <FaTrashAlt size="24" color="tomato" />
                        </ListCtn>
                    )
                })}

            </TodosListBox>
            <Footer active>
                <Footer1>
                    hello
                </Footer1>
                <Footer2>
                    <ul>
                        <li>1</li>
                        <li>2</li>
                        <li>3</li>
                        <li>4</li>
                        <li>5</li>
                        <li>6</li>
                    </ul>
                </Footer2>
            </Footer>
        </TodosListCtn>
    )
}

const TodosListCtn = styled.div`
    display: flex;
    flex-direction: column;
    gap: 20px;
    padding: 20px;
    height: 100%;
    box-sizing: border-box;
    position: relative;
`
const HomeMenuName = styled.h2`
    margin-top: 40px;
    font-size: 3rem;
    font-weight: 400;
    margin-bottom: 20px;
`
const TodosListBox = styled.div`
    display: flex;
    flex-direction: column;
    gap: 20px;
    box-sizing: border-box;
`
const ListCtn = styled.div`
    border: 1px solid #ddd;
    border-radius: 10px;
    padding: 20px;
    display: flex;
    justify-content: space-between;
    align-items: center;
`
const ListCtn2 = styled.div`
    display: flex;
    flex-direction: column;
    gap: 5px;
`
const ListTitle = styled.h3`
    font-size: 20px;
    font-weight: 400;
`
const ListNickname = styled.p`
    font-size: 12px;
    color: gray;
`
const Footer = styled.div`
    border: 1px solid red;
    background-color: red;
    color: white;
    font-size: 3em;
    text-align: center;
    width: 100%;
    height: auto;
    position: fixed;
    bottom: 0;
    left: 0;
    transition: all, 0.3s;
    &:hover {
        bottom: 0px;
        &:after {
            height: auto;
        }
    }
`


const Footer1 = styled.div`

`
const Footer2 = styled.div`
    height: -10px;
    &:hover {
        height: 100px;
        overflow: auto;
    }
`

export default TodosList
