import React, { useEffect, useState } from "react"
import styled from "styled-components"

const CommentsList = () => {
    const dispatch = useDispatch()

    return (
        <CommentsListCtn>
            <Footer>
                <Footer1>
                    댓글 보기
                </Footer1>
                <Footer2>
                    <AddCommentNickname
                        type="text"
                        name="nickname"
                        value={nickname}
                        onChange={onChangeNicknameHandler}
                        placeholder="작성자의 이름을 입력해주세요. (5자 이내)"
                        maxLength="5"
                        required
                    />
                    <AddCommentContent
                        type="text"
                        name="content"
                        value={content}
                        onChange={onChangeContentHandler}
                        placeholder="내용을 입력해주세요. (200자 이내)"
                        maxLength="200"
                        required
                    />
                    <AddCommentBtn>추가하기</AddCommentBtn>
                </Footer2>
                <CommentsCtn>
                    <CommentBox>
                        <CommentViewBox>
                            <CommentsNickname>@{"nickname1"}</CommentsNickname>
                            <CommentsContent>{"댓글이 여기에 보입니다.1"}</CommentsContent>
                        </CommentViewBox>
                        <CommentEditBox>
                            <CommentIcon
                                color="#1a73e8"
                            >
                                <FaPencilAlt
                                    size={20}
                                    color="white"
                                />
                            </CommentIcon>
                            <CommentIcon
                                color="tomato"
                            >
                                <FaTrashAlt
                                    size={20}
                                    color="white"
                                />
                            </CommentIcon>
                        </CommentEditBox>
                    </CommentBox>
                    <CommentBox>
                        <CommentViewBox>
                            <CommentsNickname>@{"nickname2"}</CommentsNickname>
                            <CommentsContent>{"댓글이 여기에 보입니다.2"}</CommentsContent>
                        </CommentViewBox>
                        <CommentEditBox>
                            <CommentIcon
                                color="#1a73e8"
                            >
                                <FaPencilAlt
                                    size={20}
                                    color="white"
                                />
                            </CommentIcon>
                            <CommentIcon
                                color="tomato"
                            >
                                <FaTrashAlt
                                    size={20}
                                    color="white"
                                />
                            </CommentIcon>
                        </CommentEditBox>
                    </CommentBox>
                    <CommentBox>
                        <CommentViewBox>
                            <CommentsNickname>@{"nickname3"}</CommentsNickname>
                            <CommentsContent>{"댓글이 여기에 보입니다.3"}</CommentsContent>
                        </CommentViewBox>
                        <CommentEditBox>
                            <CommentIcon
                                color="#1a73e8"
                            >
                                <FaPencilAlt
                                    size={20}
                                    color="white"
                                />
                            </CommentIcon>
                            <CommentIcon
                                color="tomato"
                            >
                                <FaTrashAlt
                                    size={20}
                                    color="white"
                                />
                            </CommentIcon>
                        </CommentEditBox>
                    </CommentBox>

                </CommentsCtn>
            </Footer>
        </CommentsListCtn>
    )
}
const CommentsListCtn = styled.div``
const Footer = styled.div`
    border-top: 1px solid #ddd;
    background-color: white;
    color: gray;
    width: 100%;
    font-size: 2em;
    height: 1.1em;
    position: fixed;
    bottom: 0;
    left: 0;
    transition: all, 0.3s;
    box-sizing: border-box;
    box-shadow: 0 0 5px 1px rgba(0,0,0,0.2);
    &:hover {
        height: 288px;
    }
`

const Footer1 = styled.div`
    padding: 5px 20px;
    font-size: 20px;
`
const Footer2 = styled.div`
    display: flex;
    gap: 10px;
    padding: 5px 20px;
    border-top: 1px solid #ddd;
    width: 100%;
    padding-top: 10px;
    color: #ddd;
`
const AddCommentNickname = styled.input`
    min-width: 100px;
    width: 30%;
    max-width: 300px;
    border: 1px solid #ddd;
    border-radius: 10px;
    padding: 0 12px;
    height: 46px;
    font-size: 14px;
`
const AddCommentContent = styled.input`
    width: 70%;
    border: 1px solid #ddd;
    border-radius: 10px;
    padding: 0 12px;
    height: 46px;
    font-size: 14px;
`
const AddCommentBtn = styled.button`
    background-color: transparent;
    border: 2px solid #1a73e8;
    color: #1a73e8;
    font-weight: 700;
    width: 100px;
    height: 46px;
    border-radius: 10px;
    padding: 10px;
    transition: all, 0.3s;
    &:hover {
        cursor: pointer;
        background-color: #1a73e8;
        color: white;
    }
`
const CommentsCtn = styled.div`
    display: flex;
    flex-direction: column;
    padding: 5px 20px;
    gap: 10px;
    height: 190px;
    overflow: auto;
`
const CommentBox = styled.div`
    display: flex;
    gap: 10px;
    color: black;
    width: 100%;
    border-bottom: 1px solid #ddd;
`
const CommentViewBox = styled.div`
    display: flex;
    flex-direction: column;
    padding: 10px 0;
    gap: 10px;
    color: black;
    width: 100%;
`
const CommentsNickname = styled.p`
    font-size: 14px;
`
const CommentsContent = styled.p`
    font-size: 18px;
`
const CommentEditBox = styled.div`
    display: flex;
    align-items: center;
    gap: 10px;
`
const CommentIcon = styled.div`
    border-radius: 10px;
    width: 30px;
    height: 30px;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: ${(props) => props.color};
    &:hover {
        cursor: pointer;
    }
`
export default CommentsList
