import React, { useEffect } from "react"
import { useDispatch, useSelector } from "react-redux"
import { useParams, useNavigate } from "react-router-dom"
import styled from "styled-components"
import { __getTodo } from "../../redux/modules/todoSlice"

const DetailTodo = () => {
    const dispatch = useDispatch()
    const navigate = useNavigate()

    const {isLoading, todo, error} = useSelector((state) => state.todo)
    const {id} = useParams()

    useEffect(()=> {
        dispatch(__getTodo(id))
    },[dispatch])

    if (isLoading) {
        return <div>로딩 중 ....</div>
    }

    if (error) {
        return <div>{error.message}</div>
    }

    console.log(todo)
    console.log(id)
    return (
        <DetailTodoCtn>
            <DetailHeader>
                <DetailTodoID>ID: {id}</DetailTodoID>
                <BackBtn onClick={()=>navigate("/todos")}>이전으로</BackBtn>
            </DetailHeader>
            <DetailBody>
                <DetailNickname>@{todo.nickname}</DetailNickname>
                <DetailTitle>{todo.title}</DetailTitle>
                <DetailContent>{todo.content}</DetailContent>
            </DetailBody>
            <Footer>
                <Footer1>
                    댓글 보기
                </Footer1>
                <Footer2>
                    <ul>
                        <li>1</li>
                        <li>2</li>
                        <li>2</li>
                        <li>2</li>
                        <li>2</li>
                        <li>2</li>
                        <li>2</li>
                        <li>2</li>
                        <li>2</li>
                        <li>2</li>
                        <li>2</li>
                        <li>2</li>
                        <li>2</li>
                    </ul>
                </Footer2>
            </Footer>
        </DetailTodoCtn>
    )
}

const DetailTodoCtn = styled.div`
    margin: 20px auto 0 auto;
    box-sizing: border-box;
    padding: 20px;
    display: flex;
    gap: 20px;
    flex-direction: column;
    justify-content: space-between;
`
const DetailHeader = styled.div`
    font-size: 24px;
    display: flex;
    justify-content: space-between;
    align-items: center;
`
const DetailTodoID = styled.p`
    color: gray;
`
const BackBtn = styled.button`
    border: 2px solid #ddd;
    border-radius: 10px;
    padding: 5px 12px;
`
const DetailBody = styled.div`
    border-top: 2px solid #ddd;
    padding: 40px 0;
    display: flex;
    gap: 20px;
    flex-direction: column;
`
const DetailNickname = styled.h3``
const DetailTitle = styled.h1``
const DetailContent = styled.p`
    font-size: 20px;
    line-height: 1.5;
`

const Footer = styled.div`
    border-top: 1px solid #ddd;
    padding: 0 10px;
    background-color: white;
    color: gray;
    width: 100%;
    font-size: 2em;
    height: 1.1em;
    position: fixed;
    bottom: 0;
    left: 0;
    transition: all, 0.3s;
    box-sizing: border-box;
    &:hover {
        height: 200px;
    }
`

const Footer1 = styled.div`
    padding: 4px;
    font-size: 20px;
`
const Footer2 = styled.div`
    border-top: 1px solid #ddd;
    width: 100%;
    padding-top: 10px;
    color: #ddd;
    height: 160px;
    overflow: auto;
`

export default DetailTodo
