import React, { useEffect } from "react"
import styled from "styled-components"
import { FaTrashAlt } from "react-icons/fa"
import { useDispatch, useSelector } from "react-redux"
import { __getTodos } from "../../redux/modules/todosSlice"
const TodosList = () => {
    const dispatch = useDispatch()

    const {isLoading, todos, error} = useSelector((state) => state.todos)

    useEffect(()=> {
        dispatch(__getTodos())
    },[dispatch])

    if (isLoading) {
        return <div>로딩 중 ....</div>
    }

    if (error) {
        return <div>{error.message}</div>
    }

    console.log(isLoading, todos, error)
    return (
        <TodosListCtn>
            <HomeMenuName>
                Todo List
            </HomeMenuName>
            {todos.map((todo)=> {
                <ul>
                    {console.log(todo)}
                    <div>hello</div>
                    <div>{todo.title}</div>
                    <div>{todo.content}</div>
                </ul>
            })}
            <TodosListBox>
                <ListCtn>
                    <ListCtn2>
                        <ListTitle>{"가장 최신 게시글이 위로 올라갑니다."}</ListTitle>
                        <ListNickname>@{"홍길동"}</ListNickname>
                    </ListCtn2>
                    <FaTrashAlt size="24" color="tomato" />
                </ListCtn>
                <ListCtn>
                    <ListCtn2>
                        <ListTitle>{"id: 1 글이라고 가정합니다."}</ListTitle>
                        <ListNickname>@{"todos"}</ListNickname>
                    </ListCtn2>
                    <FaTrashAlt size="24" color="tomato" />
                </ListCtn>
            </TodosListBox>
        </TodosListCtn>
    )
}

const TodosListCtn = styled.div`
    display: flex;
    flex-direction: column;
    gap: 20px;
    padding: 20px;
    box-sizing: border-box;
`
const HomeMenuName = styled.h2`
    margin-top: 40px;
    font-size: 3rem;
    font-weight: 400;
    margin-bottom: 20px;
`
const TodosListBox = styled.div`
    display: flex;
    flex-direction: column;
    gap: 20px;
    box-sizing: border-box;
`
const ListCtn = styled.div`
    border: 1px solid #ddd;
    border-radius: 10px;
    padding: 20px;
    display: flex;
    justify-content: space-between;
    align-items: center;
`
const ListCtn2 = styled.div`
    display: flex;
    flex-direction: column;
    gap: 5px;
`
const ListTitle = styled.h3`
    font-size: 20px;
    font-weight: 400;
`
const ListNickname = styled.p`
    font-size: 12px;
    color: gray;
`

export default TodosList
